/**
 * Component dependencies
 */
const React = require('react');
const PropTypes = require('prop-types');
const classNames = require('classnames');

/**
 * Component
 */
const Card = ({ className, children, raised, animated, paddingSize }) => {
  const style = paddingSize !== 'default' ? { padding: `${paddingSize}px` } : {};
  const classes = classNames('andes-card', className, {
    'andes-card--raised': raised,
    'andes-card--animated': animated,
  });
  return (
    <div style={style} className={classes}>{children}</div>
  );
};

/**
 * Prop types
 */
Card.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node.isRequired,
  raised: PropTypes.bool,
  animated: PropTypes.bool,
  paddingSize: PropTypes.oneOf(['default', 0, 16, 24, 32, 48, 64]),
};

Card.defaultProps = {
  className: '',
  raised: false,
  animated: false,
  paddingSize: 'default',
};

/**
 * Export component
 */
module.exports = Card;
