/**
 * Image dependencies
 */
const React = require('react');
const PropTypes = require('prop-types');

/**
 * Image
 */
const Image = props => (
  <img
    className={props.className}
    width={props.width}
    height={props.height}
    src={props.src}
    alt={props.title}
    srcSet={props.src2x ? `${props.src} 1x, ${props.src2x} 2x` : null}
  />);

Image.propTypes = {
  title: PropTypes.string,
  src: PropTypes.string,
  src2x: PropTypes.string,
  width: PropTypes.string,
  height: PropTypes.string,
  className: PropTypes.string,
};

Image.defaultProps = {
  title: null,
  src: null,
  src2x: null,
  width: null,
  height: null,
  className: null,
};

/**
 * Expose Image
 */
module.exports = Image;
