const React = require('react');
const PropTypes = require('prop-types');
const classNames = require('classnames');

const Widther = (WrappedComponent) => {
  class WidtherHOC extends React.PureComponent {
    render() {
      let { className } = this.props;
      const { width } = this.props;
      if (width) {
        className = classNames(className, `andes-width--${width}`);
      }
      return width !== null ? <div key="widther" className="andes-widther">
        <WrappedComponent
          {...this.props}
          className={className}
        />
      </div> : <WrappedComponent key="widther" {...this.props} />;
    }
  }

  WidtherHOC.propTypes = {
    className: PropTypes.string,
    width: PropTypes.oneOf([25, 50, 75, 100, 150, 200]),
  };

  WidtherHOC.defaultProps = {
    className: '',
    width: null,
  };

  return WidtherHOC;
};

module.exports = Widther;
